package palendrome;

public class Shorthandoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

   int a = 10 ;
   int b = 20 ;
   int c = 15 ;

	      c += a ;
	      System.out.println("c += a  = " + c );

	      c -= a ;
	      System.out.println("c -= a = " + c );

	      c *= a ;
	      System.out.println("c *= a = " + c );

	  
	      c /= a ;
	      System.out.println("c /= a = " + c );

	  
	      c %= a ;
	      System.out.println("c %= a  = " + c );

	      c <<= 2 ;
	      System.out.println("c <<= 2 = " + c );

	      c >>= 2 ;
	      System.out.println("c >>= 2 = " + c );

	    c &= a ;
	      System.out.println("c &= a  = " + c );

	      c ^= a ;
	      System.out.println("c ^= a   = " + c );

	      c |= a ;
	      System.out.println("c |= a   = " + c );
	      
	      
	      System.out.println("------------------");
	      
	      c += b ;
	      System.out.println("c += b  = " + c );

	      c -= b ;
	      System.out.println("c -= b = " + c );

	      c *= b ;
	      System.out.println("c *= b = " + c );

	  
	      c /= b ;
	      System.out.println("c /= b = " + c );

	  
	      c %= b ;
	      System.out.println("c %= b  = " + c );

	      c <<= 2 ;
	      System.out.println("c <<= 2 = " + c );

	      c >>= 2 ;
	      System.out.println("c >>= 2 = " + c );

	      c >>= 2 ;
	      System.out.println("c >>= 2 = " + c );

	      c &= b ;
	      System.out.println("c &= b  = " + c );

	      c ^= b ;
	      System.out.println("c ^= b   = " + c );

	      c |= b ;
	      System.out.println("c |= b   = " + c );
	   }
	
	}


